mapper.update:
update smart_sql
set s_time = #{sTime,jdbcType=TIMESTAMP},
  s_value = #{sValue,jdbcType=INTEGER},
  s_level = #{sLevel,jdbcType=VARCHAR}
where s_id = #{sId,jdbcType=INTEGER}

mapper.select:
select s_id, s_time, s_value, s_level
from smart_sql
where s_id = #{sId,jdbcType=INTEGER}

select * from smart_sql
ORDER BY id DESC
limit 1;

select * from smart_sql
ORDER BY id DESC
limit 1,1;
