mapper.insert:
insert into smart_smog (s_id, s_time, s_status
  )
values (#{sId,jdbcType=INTEGER}, #{sTime,jdbcType=TIMESTAMP}, #{sStatus,jdbcType=VARCHAR}
  )

mapper.update:
update smart_smog
set s_time = #{sTime,jdbcType=TIMESTAMP},
  s_status = #{sStatus,jdbcType=VARCHAR}
where s_id = #{sId,jdbcType=INTEGER}

mapper.select:
select s_id, s_time, s_status
from smart_smog
where s_id = #{sId,jdbcType=INTEGER}

select * from smart_smog
ORDER BY id DESC
limit 1;

select * from smart_smog
ORDER BY id DESC
limit 1,1;
